import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { 
  BarChart3, 
  TrendingUp, 
  Calendar,
  RefreshCw
} from "lucide-react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar, PieChart, Pie, Cell } from "recharts";
import { useState } from "react";

interface PerformanceData {
  emailsSent: Array<{ date: string; count: number; }>;
  responseRates: Array<{ date: string; rate: number; }>;
  leadsByStatus: Array<{ status: string; count: number; color: string; }>;
  monthlyTrends: Array<{ month: string; leads: number; emails: number; responses: number; }>;
}

const CHART_COLORS = {
  primary: "hsl(var(--primary))",
  success: "hsl(var(--success))",
  warning: "hsl(var(--warning))",
  destructive: "hsl(var(--destructive))",
  muted: "hsl(var(--muted-foreground))"
};

function EmailTrendsChart({ data }: { data: PerformanceData["emailsSent"] }) {
  if (!data.length) return null;

  return (
    <div className="h-64 w-full">
      <ResponsiveContainer width="100%" height="100%">
        <LineChart data={data}>
          <CartesianGrid strokeDasharray="3 3" className="opacity-30" />
          <XAxis 
            dataKey="date" 
            tick={{ fontSize: 12 }}
            tickFormatter={(value) => new Date(value).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
          />
          <YAxis tick={{ fontSize: 12 }} />
          <Tooltip 
            labelFormatter={(value) => new Date(value).toLocaleDateString()}
            formatter={(value: number) => [value, "Emails Sent"]}
            contentStyle={{
              backgroundColor: "hsl(var(--card))",
              border: "1px solid hsl(var(--border))",
              borderRadius: "6px"
            }}
          />
          <Line 
            type="monotone" 
            dataKey="count" 
            stroke={CHART_COLORS.primary}
            strokeWidth={2}
            dot={{ fill: CHART_COLORS.primary, strokeWidth: 2, r: 4 }}
            activeDot={{ r: 6, stroke: CHART_COLORS.primary, strokeWidth: 2 }}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

function ResponseRateChart({ data }: { data: PerformanceData["responseRates"] }) {
  if (!data.length) return null;

  return (
    <div className="h-64 w-full">
      <ResponsiveContainer width="100%" height="100%">
        <BarChart data={data}>
          <CartesianGrid strokeDasharray="3 3" className="opacity-30" />
          <XAxis 
            dataKey="date" 
            tick={{ fontSize: 12 }}
            tickFormatter={(value) => new Date(value).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
          />
          <YAxis tick={{ fontSize: 12 }} tickFormatter={(value) => `${value}%`} />
          <Tooltip 
            labelFormatter={(value) => new Date(value).toLocaleDateString()}
            formatter={(value: number) => [`${value}%`, "Response Rate"]}
            contentStyle={{
              backgroundColor: "hsl(var(--card))",
              border: "1px solid hsl(var(--border))",
              borderRadius: "6px"
            }}
          />
          <Bar 
            dataKey="rate" 
            fill={CHART_COLORS.success}
            radius={[2, 2, 0, 0]}
          />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

function LeadStatusChart({ data }: { data: PerformanceData["leadsByStatus"] }) {
  if (!data.length) return null;

  return (
    <div className="h-64 w-full">
      <ResponsiveContainer width="100%" height="100%">
        <PieChart>
          <Pie
            data={data}
            cx="50%"
            cy="50%"
            innerRadius={60}
            outerRadius={100}
            paddingAngle={2}
            dataKey="count"
          >
            {data.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.color} />
            ))}
          </Pie>
          <Tooltip 
            formatter={(value: number, name: string, props: any) => [value, props.payload.status]}
            contentStyle={{
              backgroundColor: "hsl(var(--card))",
              border: "1px solid hsl(var(--border))",
              borderRadius: "6px"
            }}
          />
        </PieChart>
      </ResponsiveContainer>
    </div>
  );
}

function PerformanceChartSkeleton() {
  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="space-y-1">
            <Skeleton className="h-5 w-40" />
            <Skeleton className="h-4 w-56" />
          </div>
          <div className="flex space-x-2">
            <Skeleton className="h-8 w-24 rounded" />
            <Skeleton className="h-8 w-8 rounded" />
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <Skeleton className="h-64 w-full rounded" />
      </CardContent>
    </Card>
  );
}

export default function PerformanceChart() {
  const [chartType, setChartType] = useState<"emails" | "responses" | "leads">("emails");

  const { data, isLoading, error, refetch, isRefetching } = useQuery<PerformanceData>({
    queryKey: ["/api/dashboard/performance"],
    refetchInterval: 120000, // Refetch every 2 minutes
  });

  if (isLoading) {
    return <PerformanceChartSkeleton />;
  }

  if (error || !data) {
    return (
      <Card className="border-destructive/50">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center space-x-2">
                <BarChart3 className="h-5 w-5 text-destructive" />
                <span>Performance Analytics</span>
              </CardTitle>
              <CardDescription>
                Unable to load performance data
              </CardDescription>
            </div>
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => refetch()}
              data-testid="button-retry-performance"
            >
              <RefreshCw className="h-4 w-4" />
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="text-center py-12">
            <BarChart3 className="h-8 w-8 text-destructive mx-auto mb-2" />
            <p className="text-sm text-muted-foreground">
              Failed to load performance analytics. Please try again.
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  const hasData = data.emailsSent.length > 0 || data.responseRates.length > 0 || data.leadsByStatus.length > 0;

  if (!hasData) {
    return (
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center space-x-2">
                <BarChart3 className="h-5 w-5 text-primary" />
                <span>Performance Analytics</span>
              </CardTitle>
              <CardDescription>
                Track your outreach campaign performance over time
              </CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="text-center py-12">
            <div className="p-3 rounded-full bg-muted/50 w-fit mx-auto mb-4">
              <BarChart3 className="h-8 w-8 text-muted-foreground" />
            </div>
            <h3 className="font-medium mb-2">No performance data yet</h3>
            <p className="text-sm text-muted-foreground mb-4">
              Send some emails and track responses to see your performance analytics.
            </p>
            <Button size="sm" data-testid="button-start-campaign">
              Start Your First Campaign
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  const chartOptions = [
    { value: "emails", label: "Email Trends", icon: TrendingUp },
    { value: "responses", label: "Response Rates", icon: BarChart3 },
    { value: "leads", label: "Lead Status", icon: Calendar }
  ] as const;

  const renderChart = () => {
    switch (chartType) {
      case "emails":
        return <EmailTrendsChart data={data.emailsSent} />;
      case "responses":
        return <ResponseRateChart data={data.responseRates} />;
      case "leads":
        return <LeadStatusChart data={data.leadsByStatus} />;
      default:
        return <EmailTrendsChart data={data.emailsSent} />;
    }
  };

  return (
    <Card className="card-hover" data-testid="performance-chart-card">
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="flex items-center space-x-2">
              <BarChart3 className="h-5 w-5 text-primary" />
              <span>Performance Analytics</span>
              <Badge variant="secondary" className="text-xs px-1.5 py-0.5">
                Live
              </Badge>
            </CardTitle>
            <CardDescription>
              Track your outreach campaign performance over time
            </CardDescription>
          </div>
          <div className="flex items-center space-x-2">
            <Select value={chartType} onValueChange={(value: any) => setChartType(value)}>
              <SelectTrigger className="w-40" data-testid="select-chart-type">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {chartOptions.map((option) => {
                  const Icon = option.icon;
                  return (
                    <SelectItem key={option.value} value={option.value}>
                      <div className="flex items-center space-x-2">
                        <Icon className="h-4 w-4" />
                        <span>{option.label}</span>
                      </div>
                    </SelectItem>
                  );
                })}
              </SelectContent>
            </Select>
            <Button 
              variant="ghost" 
              size="sm"
              onClick={() => refetch()}
              disabled={isRefetching}
              data-testid="button-refresh-performance"
            >
              <RefreshCw className={`h-4 w-4 ${isRefetching ? 'animate-spin' : ''}`} />
            </Button>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {renderChart()}
      </CardContent>
    </Card>
  );
}

client/src/components/dashboard/quick-ac.content:
import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Plus,
  Zap,
  Calendar,
  Upload,
  Send,
  Sparkles,
  Clock,
  FileText,
  Mail
} from "lucide-react";
import { Link, useLocation } from "wouter";

const quickActionButtons = [
  {
    id: "new-lead",
    title: "Add Lead",
    description: "Quick lead entry",
    icon: Plus,
    color: "bg-blue-500 hover:bg-blue-600",
    variant: "default" as const
  },
  {
    id: "generate-email",
    title: "AI Email",
    description: "Generate with AI",
    icon: Zap,
    color: "bg-purple-500 hover:bg-purple-600",
    variant: "default" as const,
    badge: "AI"
  },
  {
    id: "schedule-campaign",
    title: "Schedule",
    description: "Plan follow-ups",
    icon: Calendar,
    color: "bg-green-500 hover:bg-green-600",
    variant: "default" as const
  },
  {
    id: "import-leads",
    title: "Import CSV",
    description: "Bulk upload",
    icon: Upload,
    color: "bg-orange-500 hover:bg-orange-600",
    variant: "outline" as const
  }
];

interface NewLeadFormData {
  name: string;
  email: string;
  company: string;
  role: string;
  notes: string;
}

interface GenerateEmailFormData {
  leadId: string;
  tone: "professional" | "casual" | "direct";
  isFollowUp: boolean;
}

export default function QuickActions() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [openDialog, setOpenDialog] = useState<string | null>(null);
  const [, setLocation] = useLocation();

  const [newLeadData, setNewLeadData] = useState<NewLeadFormData>({
    name: "",
    email: "",
    company: "",
    role: "",
    notes: ""
  });

  const [emailData, setEmailData] = useState<GenerateEmailFormData>({
    leadId: "",
    tone: "professional",
    isFollowUp: false
  });

  // Create lead mutation
  const createLeadMutation = useMutation({
    mutationFn: async (data: NewLeadFormData) => {
      const response = await fetch("/api/leads", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to create lead");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: "Success",
        description: "Lead created successfully!",
      });
      setOpenDialog(null);
      setNewLeadData({
        name: "",
        email: "",
        company: "",
        role: "",
        notes: ""
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Generate email mutation
  const generateEmailMutation = useMutation({
    mutationFn: async (data: GenerateEmailFormData) => {
      const response = await fetch("/api/ai/generate-email", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to generate email");
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Email Generated!",
        description: "AI has created a personalized email for you.",
      });
      setOpenDialog(null);
      // Here you could open an email compose dialog with the generated content
      console.log("Generated email:", data);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleAction = (actionId: string) => {
    switch (actionId) {
      case "new-lead":
        setOpenDialog("new-lead");
        break;
      case "generate-email":
        setOpenDialog("generate-email");
        break;
      case "schedule-campaign":
        // Navigate to campaigns page where users can schedule follow-ups
        setLocation("/campaigns");
        break;
      case "import-leads":
        // Trigger file input
        const fileInput = document.createElement("input");
        fileInput.type = "file";
        fileInput.accept = ".csv";
        fileInput.onchange = handleFileUpload;
        fileInput.click();
        break;
    }
  };

  const handleFileUpload = async (event: Event) => {
    const target = event.target as HTMLInputElement;
    const file = target.files?.[0];

    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("/api/leads/import", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to import leads");
      }

      const result = await response.json();
      queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });

      toast({
        title: "Import Successful",
        description: result.message,
      });
    } catch (error: any) {
      toast({
        title: "Import Failed",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const handleCreateLead = () => {
    if (!newLeadData.name || !newLeadData.email || !newLeadData.company) {
      toast({
        title: "Missing Information",
        description: "Please fill in name, email, and company fields.",
        variant: "destructive",
      });
      return;
    }

    createLeadMutation.mutate(newLeadData);
  };

  const handleGenerateEmail = () => {
    if (!emailData.leadId) {
      toast({
        title: "No Lead Selected",
        description: "Please select a lead to generate email for.",
        variant: "destructive",
      });
      return;
    }

    generateEmailMutation.mutate(emailData);
  };

  return (
    <Card className="card-hover">
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          <Sparkles className="h-5 w-5 text-primary" />
          <span>Quick Actions</span>
        </CardTitle>
        <CardDescription>
          Fast-track your outreach workflow
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-3">
        {quickActionButtons.map((action) => {
          const Icon = action.icon;
          return (
            <Button
              key={action.id}
              variant={action.variant}
              className={`w-full justify-start h-auto py-3 px-4 ${
                action.variant === "default" ? action.color : ""
              }`}
              onClick={() => handleAction(action.id)}
              data-testid={`button-quick-${action.id}`}
            >
              <div className="flex items-center space-x-3 w-full">
                <Icon className="h-4 w-4 shrink-0" />
                <div className="flex-1 text-left">
                  <div className="flex items-center space-x-2">
                    <span className="font-medium">{action.title}</span>
                    {action.badge && (
                      <Badge variant="secondary" className="text-xs px-1.5 py-0.5">
                        {action.badge}
                      </Badge>
                    )}
                  </div>
                  <div className="text-xs opacity-75">{action.description}</div>
                </div>
              </div>
            </Button>
          );
        })}

        {/* Recent Activities */}
        <div className="pt-4 border-t">
          <h4 className="text-sm font-medium mb-2 flex items-center space-x-2">
            <Clock className="h-4 w-4" />
            <span>Recent</span>
          </h4>
          <div className="space-y-2 text-xs text-muted-foreground">
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
              <span>3 new leads added today</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
              <span>12 emails sent this week</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-purple-500 rounded-full"></div>
              <span>2 follow-ups scheduled</span>
            </div>
          </div>
        </div>
      </CardContent>

      {/* New Lead Dialog */}
      <Dialog open={openDialog === "new-lead"} onOpenChange={(open) => !open && setOpenDialog(null)}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle>Add New Lead</DialogTitle>
            <DialogDescription>
              Enter lead information to add them to your outreach pipeline.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="name">Full Name *</Label>
              <Input
                id="name"
                placeholder="John Smith"
                value={newLeadData.name}
                onChange={(e) => setNewLeadData({ ...newLeadData, name: e.target.value })}
                data-testid="input-lead-name"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="email">Email Address *</Label>
              <Input
                id="email"
                type="email"
                placeholder="john@company.com"
                value={newLeadData.email}
                onChange={(e) => setNewLeadData({ ...newLeadData, email: e.target.value })}
                data-testid="input-lead-email"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="company">Company *</Label>
              <Input
                id="company"
                placeholder="Acme Corp"
                value={newLeadData.company}
                onChange={(e) => setNewLeadData({ ...newLeadData, company: e.target.value })}
                data-testid="input-lead-company"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="role">Role/Position</Label>
              <Input
                id="role"
                placeholder="Marketing Director"
                value={newLeadData.role}
                onChange={(e) => setNewLeadData({ ...newLeadData, role: e.target.value })}
                data-testid="input-lead-role"
              />
            </div>
