import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { EmailCampaign, Lead } from "@shared/schema";

interface EmailTrackerProps {
  campaignId?: string;
  leadId?: string;
}

export default function EmailTracker({ campaignId, leadId }: EmailTrackerProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedLead, setSelectedLead] = useState<string>("");

  const { data: campaigns, isLoading: campaignsLoading } = useQuery<EmailCampaign[]>({
    queryKey: ["/api/email-campaigns"],
  });

  const { data: leads } = useQuery<Lead[]>({
    queryKey: ["/api/leads"],
  });

  const sendEmailMutation = useMutation({
    mutationFn: async ({ campaignId, leadId }: { campaignId: string; leadId: string }) => {
      const response = await apiRequest("POST", `/api/campaigns/${campaignId}/send`, { leadId });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Email sent!",
        description: "The email has been sent successfully and is being tracked.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
    },
    onError: (error) => {
      toast({
        title: "Failed to send email",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const markRepliedMutation = useMutation({
    mutationFn: async ({ campaignId }: { campaignId: string }) => {
      const response = await apiRequest("POST", `/api/email-campaigns/${campaignId}/mark-replied`);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Marked as replied!",
        description: "The campaign has been marked as replied.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/email-campaigns"] });
    },
    onError: (error) => {
      toast({
        title: "Failed to mark as replied",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const scheduleFollowUpMutation = useMutation({
    mutationFn: async ({ campaignId, delay }: { campaignId: string; delay: number }) => {
      const response = await apiRequest("POST", `/api/email-campaigns/${campaignId}/schedule-followup`, { delayDays: Math.ceil(delay / 86400) });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Follow-up scheduled!",
        description: "The follow-up email has been scheduled successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
    },
    onError: (error) => {
      toast({
        title: "Failed to schedule follow-up",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const getStatusBadge = (status: string | null) => {
    const statusColors = {
      draft: "secondary",
      sent: "default",
      opened: "outline",
      replied: "default",
      bounced: "destructive"
    } as const;

    const color = statusColors[status as keyof typeof statusColors] || "secondary";

    return (
      <Badge variant={color}>
        {status || "draft"}
      </Badge>
    );
  };

  const getStatusIcon = (status: string | null) => {
    switch (status) {
      case "sent":
        return "ðŸ“¤";
      case "opened":
        return "ðŸ‘ï¸";
      case "replied":
        return "ðŸ’¬";
      case "bounced":
        return "âŒ";
      default:
        return "ðŸ“";
    }
  };

  if (campaignsLoading) {
    return (
      <Card>
        <CardContent className="p-8">
          <div className="text-center">
            <div className="w-8 h-8 border-4 border-primary-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
            <p className="text-gray-600">Loading email campaigns...</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <span className="mr-3">ðŸ“Š</span>
            Email Campaign Tracking
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {campaigns && campaigns.length > 0 ? (
              campaigns.map((campaign) => (
                <div key={campaign.id} className="p-4 border rounded-lg bg-gray-50">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center space-x-3">
                      <span className="text-2xl">{getStatusIcon(campaign.status)}</span>
                      <div>
                        <h4 className="font-semibold text-gray-900">{campaign.subject}</h4>
                        <p className="text-sm text-gray-600">
                          {campaign.isFollowUp ? "Follow-up" : "Initial"} â€¢ Sequence #{campaign.followUpSequence || 0}
                        </p>
                      </div>
                    </div>
                    {getStatusBadge(campaign.status)}
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                    <div className="text-center p-3 bg-white rounded border">
                      <p className="text-2xl font-bold text-blue-600">
                        {campaign.sentAt ? "1" : "0"}
                      </p>
                      <p className="text-sm text-gray-600">Sent</p>
                    </div>
                    <div className="text-center p-3 bg-white rounded border">
                      <p className="text-2xl font-bold text-green-600">
                        {campaign.openedAt ? "1" : "0"}
                      </p>
                      <p className="text-sm text-gray-600">Opened</p>
                    </div>
                    <div className="text-center p-3 bg-white rounded border">
                      <p className="text-2xl font-bold text-purple-600">
                        {campaign.repliedAt ? "1" : "0"}
                      </p>
                      <p className="text-sm text-gray-600">Replied</p>
                    </div>
                  </div>

                  <div className="flex items-center justify-between">
                    <div className="text-sm text-gray-600">
                      {campaign.sentAt && (
                        <p>Sent: {new Date(campaign.sentAt).toLocaleString()}</p>
                      )}
                      {campaign.openedAt && (
                        <p>Opened: {new Date(campaign.openedAt).toLocaleString()}</p>
                      )}
                    </div>

                    <div className="flex space-x-2">
                      {campaign.status === "draft" && leads && (
                        <>
                          <Select value={selectedLead} onValueChange={setSelectedLead}>
                            <SelectTrigger className="w-48">
                              <SelectValue placeholder="Select lead" />
                            </SelectTrigger>
                            <SelectContent>
                              {leads.map((lead) => (
                                <SelectItem key={lead.id} value={lead.id}>
                                  {lead.name} ({lead.company})
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <Button
                            onClick={() => sendEmailMutation.mutate({ 
                              campaignId: campaign.id, 
                              leadId: selectedLead 
                            })}
                            disabled={!selectedLead || sendEmailMutation.isPending}
                            size="sm"
                          >
                            {sendEmailMutation.isPending ? "Sending..." : "Send Email"}
                          </Button>
                        </>
                      )}

                      {(campaign.status === "sent" || campaign.status === "opened") && (
                        <>
                          <Button
                            onClick={() => markRepliedMutation.mutate({ campaignId: campaign.id })}
                            disabled={markRepliedMutation.isPending}
                            size="sm"
                            variant="outline"
                          >
                            {markRepliedMutation.isPending ? "Marking..." : "Mark as Replied"}
                          </Button>
                          {!campaign.isFollowUp && (
                            <Button
                              onClick={() => scheduleFollowUpMutation.mutate({ 
                                campaignId: campaign.id, 
                                delay: 86400 // 24 hours
                              })}
                              disabled={scheduleFollowUpMutation.isPending}
                              size="sm"
                              variant="outline"
                            >
                              {scheduleFollowUpMutation.isPending ? "Scheduling..." : "Schedule Follow-up"}
                            </Button>
                          )}
                        </>
                      )}
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8">
                <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <span className="text-2xl">ðŸ“§</span>
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">No email campaigns yet</h3>
                <p className="text-gray-500">
                  Create your first email campaign to start tracking opens, clicks, and replies.
                </p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/layout/header.tsx.content:
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Badge } from "@/components/ui/badge";
import { Bell, Search, Plus, LogOut, User, Settings, Moon, Sun } from "lucide-react";
import { Input } from "@/components/ui/input";
import { useState } from "react";
import { useLocation } from "wouter";

interface HeaderProps {
  title: string;
  subtitle?: string;
  actions?: React.ReactNode;
}

export default function Header({ title, subtitle, actions }: HeaderProps) {
  const { user } = useAuth();
  const [theme, setTheme] = useState<"light" | "dark">("light");
  const [, setLocation] = useLocation();

  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    document.documentElement.classList.toggle("dark", newTheme === "dark");
  };

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  const getUserInitials = () => {
    if (!user?.firstName && !user?.lastName) return "U";
    return `${user?.firstName?.[0] || ""}${user?.lastName?.[0] || ""}`.toUpperCase();
  };

  return (
    <header className="h-16 border-b border-border bg-card/50 backdrop-blur supports-[backdrop-filter]:bg-card/50">
      <div className="flex items-center justify-between h-full px-6">
        {/* Left side - Title and search */}
        <div className="flex items-center space-x-4 flex-1">
          <div className="min-w-0">
            <h1 className="text-2xl font-semibold tracking-tight text-foreground">
              {title}
            </h1>
            {subtitle && (
              <p className="text-sm text-muted-foreground mt-0.5">
                {subtitle}
              </p>
            )}
          </div>

          <div className="hidden md:flex items-center space-x-2 ml-8">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Search leads, campaigns..."
                className="pl-10 w-64 bg-background/50"
                data-testid="input-global-search"
              />
            </div>
          </div>
        </div>

        {/* Right side - Actions and user menu */}
        <div className="flex items-center space-x-3">
          {actions}

          <Button
            variant="outline"
            size="sm"
            className="hidden sm:flex items-center space-x-2 bg-primary text-primary-foreground hover:bg-primary/90 border-primary"
            data-testid="button-new-lead"
            onClick={() => setLocation('/leads')}
          >
            <Plus className="h-4 w-4" />
            <span>New Lead</span>
          </Button>

          {/* Notifications */}
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="icon" className="relative" data-testid="button-notifications">
                <Bell className="h-4 w-4" />
                <Badge className="absolute -top-1 -right-1 h-5 w-5 p-0 flex items-center justify-center text-xs bg-destructive text-destructive-foreground">
                  3
                </Badge>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end" className="w-80">
              <DropdownMenuLabel className="flex items-center justify-between">
                <span>Notifications</span>
                <Badge variant="secondary" className="text-xs">3 new</Badge>
              </DropdownMenuLabel>
              <DropdownMenuSeparator />

              <div className="p-2 space-y-2">
                <div className="p-2 rounded-lg bg-blue-50 dark:bg-blue-950/50 border border-blue-200 dark:border-blue-800">
                  <p className="text-sm font-medium">New email reply received</p>
                  <p className="text-xs text-muted-foreground">John Smith replied to your outreach</p>
                </div>

                <div className="p-2 rounded-lg bg-green-50 dark:bg-green-950/50 border border-green-200 dark:border-green-800">
                  <p className="text-sm font-medium">Campaign completed</p>
                  <p className="text-xs text-muted-foreground">Q4 Outreach campaign finished successfully</p>
                </div>

                <div className="p-2 rounded-lg bg-amber-50 dark:bg-amber-950/50 border border-amber-200 dark:border-amber-800">
                  <p className="text-sm font-medium">Follow-up due</p>
                  <p className="text-xs text-muted-foreground">5 leads need follow-up today</p>
                </div>
              </div>
            </DropdownMenuContent>
          </DropdownMenu>

          {/* Theme toggle */}
          <Button
            variant="ghost"
            size="icon"
            onClick={toggleTheme}
            data-testid="button-theme-toggle"
            className="text-foreground hover:bg-accent hover:text-accent-foreground border-0"
          >
            {theme === "light" ? (
              <Moon className="h-4 w-4" />
            ) : (
              <Sun className="h-4 w-4" />
            )}
          </Button>

          {/* User menu */}
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" className="relative h-8 w-8 rounded-full" data-testid="button-user-menu">
                <Avatar className="h-8 w-8">
                  <AvatarImage src={user?.profileImageUrl} alt={user?.firstName || "User"} />
                  <AvatarFallback className="bg-primary text-primary-foreground text-xs">
                    {getUserInitials()}
                  </AvatarFallback>
                </Avatar>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent className="w-56" align="end" forceMount>
              <DropdownMenuLabel className="font-normal">
                <div className="flex flex-col space-y-1">
                  <p className="text-sm font-medium leading-none">
                    {user?.firstName} {user?.lastName}
                  </p>
                  <p className="text-xs leading-none text-muted-foreground">
                    {user?.email}
                  </p>
                  <Badge variant="secondary" className="w-fit text-xs capitalize mt-1">
                    {user?.role || "user"}
                  </Badge>
                </div>
              </DropdownMenuLabel>
              <DropdownMenuSeparator />
              <DropdownMenuItem data-testid="menu-item-profile">
                <User className="mr-2 h-4 w-4" />
                <span>Profile</span>
              </DropdownMenuItem>
              <DropdownMenuItem data-testid="menu-item-settings">
                <Settings className="mr-2 h-4 w-4" />
                <span>Settings</span>
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem 
                onClick={handleLogout}
                className="text-destructive focus:text-destructive"
                data-testid="menu-item-logout"
              >
                <LogOut className="mr-2 h-4 w-4" />
                <span>Log out</span>
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </header>
  );
}

